import React from "react";
import Form from "react-bootstrap/Form";
import { Col, Button, Row, Spinner } from "react-bootstrap";
import { Container } from "react-bootstrap";
import image from "../Files/images/register.jpg";
import rules from "../Files/validationRules.json";
import DICTIONARY from "../Files/dictionary.json";
import {
    formValidation,
    validationFeedback,
    validationClassName,
} from "../Validator";

class Home extends React.Component {
    state = {
        form: {
            isValid: false,
            elements: {
                firstName: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.firstName,
                },
                lastName: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.lastName,
                },
                email: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.email,
                },
                password: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.password,
                },
                city: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.city,
                },
                street: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.street,
                },
                houseNumber: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.houseNumber,
                },
                zipCode: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.zipCode,
                },
                country: {
                    touched: false,
                    valid: false,
                    validationRules: rules.registration.country,
                },
            },
        },
        user: {
            firstName: null,
            lastName: null,
            username: "stylishslave",
            email: null,
            password: null,
            city: null,
            street: null,
            houseNumber: null,
            zipCode: [],
            country: [],
            role: "ROLE_USER",
        },
    };
    inputUpdateHandler = async (event, nameOfFormInput, select) => {
        let newState = { ...this.state.user };

        //string inputs
        if (Object.keys(newState).includes(nameOfFormInput)) {
            if (select && event.target.value == 0) {
                newState[nameOfFormInput] = null;
            } else {
                newState[nameOfFormInput] = event.target.value;
            }
        }
        //TODO: zjistit proc to trva o interval vic
        //problem je s console.log() ten vraj nema aktualny state ale ten pred jeho aktualizaciou
        //natrafil som ale aj tak na problemy ked som nieco z formularu posielal do rodicovkeho komponentu
        //a vyriesil som to tak ze pred atributy metody dopises async cize ...handler = async (event,...
        //a ked this.setState dopises await
        //console.log(newState);
        await this.setState({ user: newState });
        if (this.state.form.elements[nameOfFormInput].touched) {
            this.validateForm();
        }
        console.log(this.state.user);
    };

    validateForm = async () => {
        const newState = { ...this.state.form };
        formValidation(newState, this.state.user);
        await this.setState({ form: newState });
    };

    submitHandler = async (event) => {
        event.preventDefault();
        console.log(this.state.user);
        this.validateForm();

        /*fetch("http://localhost:8080/trip", {
            method: "POST",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(this.state.trip),
        }).then((response) => {
            if (response.ok) this.props.history.push("/trip");
            //TODO - osetrenie vynimiek
            else console.log("Error: somethhing goes wrong");
        });*/
    };
    render() {
        let countryOptions = null;
        if (DICTIONARY.countries.length > 0) {
            let countriesArray = [];

            countriesArray.push(
                <option value="0">{DICTIONARY.form.first_option}</option>
            );

            for (var i = 0; i < DICTIONARY.countries.length; i++) {
                countriesArray.push(
                    <option key={i + 1} value={DICTIONARY.countries[i]}>
                        {DICTIONARY.countries[i]}
                    </option>
                );
            }
            countryOptions = (
                <Form.Control
                    as="select"
                    placeholder="Enter name"
                    onChange={(event) =>
                        this.inputUpdateHandler(event, "country", true)
                    }
                    className={validationClassName("country", this.state.form)}
                >
                    {countriesArray}
                    <div class="invalid-feedback">
                        {validationFeedback("country", this.state.form)}
                    </div>
                </Form.Control>
            );
        }
        return (
            <Container className="register_container">
                <Row>
                    <Col xs={5}>
                        <img src={image} />
                    </Col>
                    <Col>
                        <h3>Register new user</h3>
                        <Form
                            className="registerUserForm mt-3 mb-5"
                            onSubmit={this.submitHandler}
                        >
                            <div className="window radius">
                                <h5>Account information</h5>
                                <Form.Row>
                                    <Form.Group as={Col}>
                                        <Form.Label>First name</Form.Label>
                                        <Form.Control
                                            maxLength="30"
                                            autoComplete="name"
                                            placeholder="Enter your first name"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "firstName"
                                                )
                                            }
                                            className={validationClassName(
                                                "firstName",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "firstName",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                    <Form.Group as={Col}>
                                        <Form.Label>Last name</Form.Label>
                                        <Form.Control
                                            maxLength="30"
                                            placeholder="Enter your last name"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "lastName"
                                                )
                                            }
                                            className={validationClassName(
                                                "lastName",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "lastName",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                </Form.Row>
                                <Form.Row>
                                    <Form.Group as={Col}>
                                        <Form.Label>E-mail</Form.Label>
                                        <Form.Control
                                            maxLength="50"
                                            name="email"
                                            autoComplete="email"
                                            type="email"
                                            placeholder="Enter your valid e-mail"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "email"
                                                )
                                            }
                                            className={validationClassName(
                                                "email",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "email",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                    <Form.Group as={Col}>
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control
                                            maxLength="30"
                                            minLength="8"
                                            maxLength="16"
                                            type="password"
                                            placeholder="Password"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "password"
                                                )
                                            }
                                            className={validationClassName(
                                                "password",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "password",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                </Form.Row>
                            </div>
                            <br />
                            <div className="window radius">
                                <h5>Adress</h5>
                                <Form.Row>
                                    <Form.Group as={Col}>
                                        <Form.Label>City</Form.Label>
                                        <Form.Control
                                            maxLength="100"
                                            placeholder="City"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "city"
                                                )
                                            }
                                            className={validationClassName(
                                                "city",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "city",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                    <Form.Group as={Col}>
                                        <Form.Label>Street</Form.Label>
                                        <Form.Control
                                            maxLength="50"
                                            placeholder="Street"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "street"
                                                )
                                            }
                                            className={validationClassName(
                                                "street",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "street",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                </Form.Row>
                                <Form.Row>
                                    <Form.Group as={Col}></Form.Group>
                                    <Form.Group as={Col}>
                                        <Form.Label>ZIP code</Form.Label>
                                        <Form.Control
                                            min="10000"
                                            max="99999"
                                            maxLength="5"
                                            type="number"
                                            placeholder="ZIP code"
                                            onChange={(event) =>
                                                this.inputUpdateHandler(
                                                    event,
                                                    "zipCode"
                                                )
                                            }
                                            className={validationClassName(
                                                "zipCode",
                                                this.state.form
                                            )}
                                        />
                                        <div class="invalid-feedback">
                                            {validationFeedback(
                                                "zipCode",
                                                this.state.form
                                            )}
                                        </div>
                                    </Form.Group>
                                    <Form.Group as={Col}>
                                        <Form.Label>Country</Form.Label>
                                        {countryOptions}
                                    </Form.Group>
                                    <Form.Group as={Col}></Form.Group>
                                </Form.Row>
                                <Form.Row>
                                    <Button
                                        className="submit"
                                        variant="primary"
                                        type="submit"
                                    >
                                        Create new account
                                    </Button>
                                </Form.Row>
                            </div>
                        </Form>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Home;
